parameters.jsCode:
// Verificar si el lead necesita cambio de estrategia
const lead = $input.first().json;

// Obtener estadÃ­sticas de llamadas
const totalCalls = parseInt(lead.total_calls) || 0;
const successfulCalls = parseInt(lead.successful_calls) || 0;
const callScore = parseInt(lead.call_total_score) || 0;

let acciones = [];

// Si ha fallado mÃºltiples intentos por llamada
if (totalCalls >= 3 && successfulCalls === 0) {
  acciones.push({
    tipo: 'cambio_canal',
    descripcion: 'Cambiar a WhatsApp - No responde llamadas',
    canal_nuevo: 'whatsapp'
  });
}

// Si el call score es muy bajo
if (callScore < 20 && totalCalls >= 2) {
  acciones.push({
    tipo: 'cambio_horario',
    descripcion: 'Intentar en horario diferente (tarde/noche)',
    horario_sugerido: 'tarde'
  });
}

// Si no hay engagement
if (lead.engagement_score < 15) {
  acciones.push({
    tipo: 'nurturing',
    descripcion: 'AÃ±adir a campaÃ±a de nurturing por email',
    campana: 'nurturing_bajo_engagement'
  });
}

return [{
  json: {
    lead_id: lead.lead_id,
    acciones_recomendadas: acciones,
    total_acciones: acciones.length
  }
}];

